(8, 'Entrance center published', 'Entrance center published', '<p><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Purbanchal University has published &nbsp;exam center &nbsp;for &nbsp;second entrance exam of B.E Elx &amp; Communication and MSc. in Urban Design &amp; Conservation.</span><br />\r\n<br />\r\n<span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Exam Center : White house (Satdobato,Khumaltar)</span><br />\r\n<span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Date : Mangsir 8</span><sup>th</sup><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">&nbsp;Monday</span><br />\r\n<span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Time : 8:00AM for B.E &nbsp;and 2:00 For MSc in Urban design &amp; Conservation</span></p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2014-11-25 04:19:50', '2017-07-02 08:59:30', 337, 1),
(9, 'Classes shut down during SAARC conference', 'Classes shut down during SAARC conference', '<p><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">All the classes will remain shut down for 9</span><sup>th</sup><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">&nbsp;- 12</span><sup>th</sup><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">&nbsp;Mangsir due 18</span><sup>th</sup><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">&nbsp;SAARC conference in Kathmandau.</span><br />\r\n<span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Administration &nbsp;will be opened for 9</span><sup>th</sup><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">&nbsp;and 12</span><sup>th</sup><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">&nbsp;Mangsir.</span></p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2014-11-25 04:26:46', '2017-07-03 00:28:06', 293, 1),
(10, 'Second Entrance result published', 'Merit list of Second entrance exam for B.E. Electronics & Communication', '<p><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Merit list of Second entrance exam for B.E. Electronics &amp; Communication.</span></p>\r\n\r\n<p><span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Interview Schedule&nbsp;&nbsp;: 2071 Mangsir 16th Tuesday ( 8:00AM-1:30PM)</span><br />\r\n<span style="font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Last date of admission : 2071 Mangsir 17th ( 8:00AM- 1:30 PM)&nbsp;</span></p>\r\n', 99, 12, '/uploads/notice/Notice_20141207_II_Entrance_Result.png', NULL, NULL, NULL, 1, 1, 1, '2014-12-07 03:59:47', '2017-07-03 21:43:16', 1000, 1),
(11, 'Guest Lecture on Earth, Architecture and Human', 'Guest Lecture on topic Earth, Architecture and Human is going to be held on 19th Mangsir, 2071 (10:30AM-12:30PM).', '<p style="text-align:justify">Guest Lecture on topic Earth, Architecture and Human is going to be held on 19th Mangsir, 2071 (10:30AM-12:30PM).<br />\r\nAr. Kazuya Mortia from Japan will deliver lecture on above topic.<br />\r\n<br />\r\nBreif Introduction to Ar. Kazuya Mortia</p>\r\n\r\n<ul>\r\n	<li>Born :1971&nbsp;</li>\r\n	<li>Bachelor of Architecture, Kyoto University : 1994</li>\r\n	<li>Master of Architecture, Kyoto University : 1997</li>\r\n	<li>Worked as Craftsman of traditional plaster : 1997-2001</li>\r\n	<li>Worked at EMBT ( Barcelona) : 2007-2008</li>\r\n	<li>Visiting researcher at ETSAB ( Barcelona) : 2011-2012</li>\r\n</ul>\r\n\r\n<div style="font-family: Arial, verdana, Arial, Helvetica, sans-serif; font-size: small; line-height: 15.6000003814697px; text-align: justify;"><br />\r\nVenue : Hall 1<br />\r\n<br />\r\nOrganzied by<br />\r\nDepartment of Architecture&nbsp;</div>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2014-12-07 04:00:43', '2017-07-03 07:19:23', 307, 1),
(12, 'MSc. Urban Design & Conservation second entrance result', 'MSc. Urban Design & Conservation second entrance result', '<p><strong>MSc. Urban Design &amp; Conservation second entrance result</strong></p>\r\n', 99, 12, '/uploads/notice/Notice_20140712_MEResult.jpg', NULL, NULL, NULL, 1, 1, 1, '2014-12-07 04:01:59', '2017-07-01 06:41:55', 357, 1),
(13, 'Orientation program of Master Program', 'All the newly admitted students in M.E. Earthquake and MSc. Urban Design Conservation are informed to attend in orientation and welcome program on Mangsir 25th.', '<p><span style="background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">All the newly admitted students in M.E. Earthquake and MSc. Urban Design Conservation are informed to attend in orientation and welcome program on Mangsir 25</span><sup>th</sup><span style="background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">.</span><br />\r\n<br />\r\n<span style="background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Time : 8:00 AM&nbsp;</span><br />\r\n<span style="background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Venue : Khwopa Engineering College</span></p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2014-12-11 08:03:21', '2017-07-03 18:45:47', 321, 1),
(14, 'Unit Test exam of Fourth Semester', 'Unit test of B.E/B.Arch of fourth semester will be started from 29th Mangsir. ', '<p><span style="background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small">Unit test of B.E/B.Arch of fourth semester will be started from 29</span><sup>th</sup><span style="background-color:rgb(255, 255, 255); color:rgb(51, 51, 51); font-family:arial,verdana,arial,helvetica,sans-serif; font-size:small"> Mangsir.&nbsp;</span></p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2014-12-11 08:04:01', '2017-07-01 18:18:51', 321, 1),
(15, 'Academic Calender of PU', 'Academic Calender of PU', '<p><strong>Academic Calender of PU</strong></p>\r\n', 99, 12, '/uploads/notice/Notice_20150120_AcademicCalendar_PU.jpg', NULL, NULL, NULL, 1, 1, 1, '2015-01-20 03:16:10', '2017-07-02 02:07:38', 350, 1),
(16, 'Symposium on Border of Nepal ,its security and Sovereignty of Nepal', 'A symposium program on titled " Border of Nepal and its security" and " Sovereignty of Nepal and Current Affairs "is going to be held on 25th Paush Friday. So all the faculty memebers and staffs are requested to participate on that program.', '<p>A symposium program on titled &quot; Border of Nepal and its security&quot; and &quot; Sovereignty of Nepal and Current Affairs &quot;is going to be held on 25th Paush Friday. So all the faculty memebers and staffs are requested to participate on that program.<br />\r\n&nbsp;</p>\r\n\r\n<p>Chief guest: Honorable Comrade Narayanman Bijukchhe (Rohit ), Chairman of NWPP , CA member<br />\r\nGuest: Mr. Buddhi Narayan Shrestha (Border specialist)<br />\r\nGuest: Dr. Shastra Dutta Pant (Analyst)</p>\r\n\r\n<p><br />\r\nDate : 2071 Paush 25 Friday<br />\r\nTime: 9:00AM<br />\r\nVenue: KhEC, &nbsp;Hall 1</p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2015-02-01 04:03:12', '2017-06-30 13:02:16', 302, 1),
(17, 'Earthquake Safety Week 2071', 'Earthquake Safety Week 2071', '<p><strong>Earthquake Safety Week 2071</strong></p>\r\n', 99, 12, '/uploads/notice/Notice_20150121_earthquake_safety_day.png', NULL, NULL, NULL, 1, 1, 1, '2015-02-01 04:04:29', '2017-07-04 01:33:43', 530, 1),
(18, 'Test', 'This is test from author', '<p>This is test from author</p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2015-02-09 04:30:17', '2015-08-16 08:28:44', 0, 0),
(19, 'Assessment of 5th and 7th semester', 'Assessment of 5th and 7th semester', '<p><strong>Assessment of 5th and 7th semester</strong></p>\r\n', 99, 12, '/uploads/notice/Notice_20150215_Assessment.jpg', NULL, NULL, NULL, 1, 1, 1, '2015-02-15 02:44:30', '2017-07-03 07:27:42', 339, 1),
(20, '3rd semester class start from 3rd Phalgun', 'All 3rd semester classes will start from coming 3rd Phalgun. ', '<p>All 3rd semester classes will start from coming 3rd Phalgun.&nbsp;</p>\r\n\r\n<p>College Time : 7:00AM-2:00PM&nbsp;</p>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2015-02-15 02:45:18', '2017-07-03 17:13:41', 308, 1),
(22, 'Convocation', 'Submission of Application for Convocation. \r\n', '<p>Submission of Application for Convocation.<br />\r\nPassed students are notify to submit Application form for Purbanchal University Concocation - 2071 going to be held on 30th Falgun, 2071.<br />\r\nLast date of form submission in college : 2071, Falgun 14.<br />\r\nmore in Notice.</p>\r\n', 99, 12, '/uploads/notice/Notice_20150220_Convocation.jpg', NULL, NULL, NULL, 1, 1, 1, '2015-02-20 03:46:10', '2017-07-03 21:16:34', 489, 1),
(23, 'Providing convocation fee receipt', 'Providing convocation fee receipt', '<h1>Providing convocation fee receipt</h1>\r\n', 99, 12, '', NULL, NULL, NULL, 1, 1, 1, '2015-03-18 07:14:44', '2017-07-01 09:02:29', 324, 1),
